css:
* {
    margin: 0;
    padding: 0;
  }

.print-format {
    padding: 0in;
    font-family: "Times New Roman", Times, serif;
}
.p-tag {
    margin: 0 4em;
    font-size: 0.8em;
}
.print-format p{
    font-weight:bold;
    margin : 0 0 0;

}
.print-format img {
    max-width: 100%;
    height: auto;
    display: block;
    margin: 0 auto;
    position : relative;
    /*transform :translate(-10%, 1%);*/
    /*min-height: 20vh;*/


}
.print-format td, .print-format th {
    vertical-align: middle !important;
    padding: 1px !important;
    font-weight: bold;
    color: black;
    font-size: 15px;
}


table {
    width: 85%;
    max-height: 500px;
    margin: 0 auto;
    border-collapse: collapse;
    text-align: center;

}
th, td {
    border: 1px solid black;
    padding: 8px;
    text-align: center;

}
/*.body{*/
/*    min-height:90vh;*/
/*}*/
pre{
    background: none !important;
    border: none !important;
    font-size: 16px !important;
    font-weight: bold !important;
    text-align: center !important;
    color: black !important;
    display: block;
    padding: 0 !important;
    margin: 0 0 0 !important;
    font-size: 15px !important;
    line-height: 1 !important;
}
.table-data{
    margin: 2rem;
}
table ol,
table ul{
    padding: 0;
    margin: 0;
    direction: rtl;
    list-style-position: inside;
}
table li{
    text-align: right !important;
    padding: 0 8px;
}
p{
        font-size: 12px;
}

